document.addEventListener("DOMContentLoaded",function(){const e=document.getElementById("toggle-mode"),t=document.documentElement;localStorage.getItem("theme")==="dark"?(t.classList.add("dark"),e.innerText="light_mode"):(t.classList.remove("dark"),e.innerText="dark_mode"),e.addEventListener("click",function(){t.classList.contains("dark")?(t.classList.remove("dark"),e.innerText="dark_mode",localStorage.setItem("theme","light")):(t.classList.add("dark"),e.innerText="light_mode",localStorage.setItem("theme","dark"))})}),document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".color-btn"),t=document.getElementById("product-image"),n={red:"/images/sepatu1.png",yellow:"/images/sepatu2.png",green:"/images/sepatu3.png",tosca:"/images/sepatu4.png",blue:"/images/sepatu5.png"};e.forEach(s=>{s.addEventListener("click",function(){e.forEach(e=>e.classList.remove("active")),this.classList.add("active");const s=this.id;t.src=n[s]})})})